var socket = io();

document.getElementById("jouer").onclick = function () {
  document.getElementById("prompt").style.display = "none";
  document.getElementById("game").style.display = "flex";
  const audio = new Audio();
  audio.autoplay = true;
  audio.src =
    "data:audio/mpeg;base64,";
  socket.on("audio", (msg) => {
    audio.src = msg.file;
  });
};
